namespace BillAndGoBundle\Repository;

use BillAndGoBundle\Entity\User;
use Doctrine\ORM\EntityRepository;

/**
 * DocumentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DocumentRepository extends EntityRepository
{
    /**
     * @param integer $refuser
     * @return array
     */
    public function findAllEstimate(int $refuser) : array
    {
        $qb = $this->createQueryBuilder('doc');
        $qb
            ->select('doc')
            ->where('doc.refUser = :user')
            ->setParameter('user', $refuser)
            ->andWhere('doc.type = 1');
        return $qb->getQuery()->getResult();
    }

    /**
     * @param integer $refuser
     * @return array
     */
    public function findAllBill(int $refuser) : array
    {
        $qb = $this->createQueryBuilder('doc');
        $qb
            ->select('doc')
            ->where('doc.refUser = :user')
            ->setParameter('user', $refuser)
            ->andWhere('doc.type = 0');
        return $qb->getQuery()->getResult();
    }


    /**
     * @param int $refuser
     * @param string $q
     * @return array
     */
    public function findAllDoc(int $refuser, string $q) : array
    {
        $qb = $this->createQueryBuilder('doc');
        $qb
            ->select('doc')
            ->where('doc.refUser = :user')
            ->andWhere('doc.description LIKE :q OR doc.number LIKE :q')
            ->setParameter('user', $refuser)
            ->setParameter('q', '%'.$q.'%');
        return $qb->getQuery()->getResult();
    }


    /**
     * @param int $refuser
     *
     * @param int $refClient
     * @return array
     */
    public function findAllDocByRefClient(int $refuser, int $refClient, int $limit = null) : array
    {
        $qb = $this->createQueryBuilder('doc');
        $qb
            ->select('doc')
            ->where('doc.refUser = :user')
            ->andWhere('doc.refClient = :client')
            ->setParameter('user', $refuser)
            ->setMaxResults($limit)
            ->orderBy("doc.id", "DESC")
            ->setParameter('client', $refClient);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param User $user
     * @param string $type
     * @param null $begin
     * @param null $end
     * @param array $status
     * @return array
     */
    public function findByDate(
        User $user,
        string $type,
        $begin = null,
        $end = null,
        array $status
    ) : array {
        $typeBool = ($type == 'estimate') ? 1 : 0;
        $qb = $this->createQueryBuilder('doc');
        $qb
            ->select('doc')
            ->where('doc.refUser = :user')
            ->setParameter('user', $user)
            ->andWhere('doc.type = '.$typeBool)
        ;
        if ((null !== $begin) && (!empty($begin))) {
            $qb
                ->andWhere('doc.sentDate >= :begin')
                ->setParameter('begin', new \DateTime($begin));
        }
        if ((null !== $end) && (!empty($begin))) {
            $qb
                ->andWhere('doc.sentDate <= :end')
                ->setParameter('end', new \DateTime($end));
        }
        if (!empty($status)) {
            $qb
                ->andWhere('doc.status IN (:status)')
                ->setParameter('status', $status);
        }
        return $qb->getQuery()->getResult();
    }

    /**
     * @param int $year
     * @param int $type
     * @param int $id
     * @param $connector
     * @return mixed
     */
    public function getBillOrQuote(int $year, int $type, int $id, $connector)
    {
        $sql = "select count(*) as counter, REPLACE(DATE_FORMAT(delayDate, '%m'), '0', '') as mt
                from document 
                WHERE YEAR(delayDate) = :year
                AND type = :type
                AND refUser = :id
                GROUP BY YEAR(delayDate), MONTH(delayDate)";

        $stm = $connector->prepare($sql);
        $stm->execute(["year" => $year, "type" => $type, "id" => $id]);
        return ($stm->fetchAll(\PDO::FETCH_ASSOC));
    }
}
